import { useDispatch } from "react-redux";
import { usersSliceActions } from "..";
import { loadUsers } from "./loadUsersIfNotExist";

export const addUserIfValidate = ({email, role, name, phone, city, pass}) => {

  const isDataValid = true;

  if (isDataValid) {
    // dispatch(usersSliceActions.addUser(formData));

    const options = {
      method: "POST",
      headers: {
        "Content-Type": "application/json;charset=utf-8",
      },
      body: JSON.stringify({
        user: `insert into peoples(pid,name,email,pass,idaccess) values(1,'${name}','${email}','${pass}',1);`,
      }),
    };

    fetch("http://localhost:4000/addUser", options)
      .then((res) => res.json())
      .then((data) => {
        console.log(data);
      })
      .catch((err) => console.log(err));
  }
};
