import classNames from 'classnames'
import styles from './styles.module.css'

export const Users = () => {
    const data = []

    for (let i=0; i< 50; i++) {
        data.push({'Наименование': 'UM System Group',
        "E-mail": 'example@mail.ru',
        "Телефон": '+77007007070',
        "Роль": 'admin',
        "Франшиза": 'Шымкент, Сарыагаш',
        "баланс": '250000'})
    }

    const headers = ['#'].concat(Object.keys(data[0]))
    
    return (
        data
        ? <div className={styles.table}>
            <div className={styles.table_header}>
                {headers.map((el, idx) => {
                    return (
                        <div key={idx} className={classNames(styles.table_cell, styles.table_title)}>{el.toUpperCase()}</div>
                    )
                })}
            </div>
            <div className={styles.table_content}>
                {data.map((el, index) => {
                    const cellsValues = Object.values(el);

                    return (
                        <div className={styles.table_row}>
                            <div className={classNames(styles.table_cell, styles.table_cell_count)}>{index+1}</div>
                            {cellsValues.map((cell, idx) => {
                                if (idx===cellsValues.length-1) {
                                    const balance = cell.toLocaleString()
                                    return <div key={idx+index} className={styles.table_cell}><div className={styles.table_cell__balance}>{balance}</div></div>
                                }
                                return <div key={idx+index} className={styles.table_cell}>{cell}</div>
                            })}
                        </div>
                    )
                })}
            </div>
        </div>
        : <div>no data</div>
    )
}
