import classNames from 'classnames';
import styles from './style.module.css';
import { nanoid } from 'nanoid';
import addIMG from './images/add.svg';
import editIMG from './images/edit.svg';
import deleteIMG from './images/delete.svg';
import { EditPopup } from '../EditPopup/EditPopup';
import { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useEffect } from 'react';
import { usersSliceActions } from '../../store/Users';
import { selectUsers } from '../../store/Users/selectors';

export const Users = ({ asideIsOpened }) => {
  const dispatch = useDispatch();
  useEffect(() => {
    data.forEach((el) => dispatch(usersSliceActions.addUser(el)));
  }, []);
  const users = useSelector((state) => selectUsers(state));
  const [isPopupOpened, setIsPopupOpened] = useState(false);
  const togglePopup = () => {
    setIsPopupOpened(!isPopupOpened);
  };

  const data = [];

  for (let i = 0; i < 50; i++) {
    data.push({
      name: 'UM System Group',
      email: 'example@mail.ru',
      phone: '+77007007070',
      role: 'admin',
      city: 'Шымкент, Сарыагаш',
      balance: '250000',
    });
  }

  const headers = ['#', 'НАИМЕНОВАНИЕ', 'E-mail', 'Телефон', 'Роль', 'Франшиза', 'Баланс'];

  return (
    <table className={styles.table}>
      <thead className={styles.table_header}>
        <tr className={styles.table_row}>
          {headers.map((el) => {
            return (
              <th
                key={nanoid()}
                className={classNames(styles.table_cell, styles.table_cell__header)}
              >
                {el}
              </th>
            );
          })}
        </tr>
      </thead>
      <tbody className={styles.table_content}>
        {users.map((el, row) => {
          const cellsValues = Object.values(el);
          const { balance, city, email, name, password, phone, role } = el;

          return (
            <tr className={styles.table_row}>
              {cellsValues.map((cell, idx) => {
                // if (idx === cellsValues.length - 1) {
                //   const balance = cell ? cell.replace(/\B(?=(\d{3})+(?!\d))/g, ',') : 0; // add ',' separator at balance column
                //   return (
                //     <td key={nanoid()} className={styles.table_cell}>
                //       <div className={styles.table_cell__balance}>{balance}</div>
                //     </td>
                //   );
                // } else
                //   return (
                //     <td key={nanoid()} className={styles.table_row}>
                //       {cell}
                //     </td>
                //   );
                switch (idx) {
                  default:
                    return null;

                  case 0:
                    return (
                      <td key={nanoid()} className={styles.table_row}>
                        {+row + 1}
                      </td>
                    )
                }
              })}
            </tr>
          );
        })}
      </tbody>
    </table>
  );
};
