import classNames from "classnames";
import { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { selectFranshisesIds } from "../../store/Franshises/selectors";
import { addUser } from "../../store/Users/Thunks/addUser";
import { Option } from "../Option/Option";
import styles from "./styles.module.css";
const roleVars = {
  admin: { content: "admin", id: 1 },
  user: { content: "user", id: 3 },
  master: { content: "master", id: 2 },
};

export const AddObjectPopup = ({ togglePopup }) => {
  const dispatch = useDispatch();
  const franshisesIds = useSelector(state => selectFranshisesIds(state))
  const [form, setForm] = useState({
    name: '',
    idFran: '',
    orgOwner: '',
    phone: '',
    worker: '',
  })
  
  const userId = localStorage.userId;
 

  const onSubmit = (event) => {
    event.preventDefault();
  };

  return (
    <div className={styles.popup_wrapper}>
      <form className={styles.form} onSubmit={onSubmit}>
        <h2 className={styles.title}>Добавление объекта</h2>
        <button
          type="button"
          className={classNames(styles.button, styles.popup_closeButton)}
          onClick={() => togglePopup()}
        />
        <div className={styles.input_container}>
          <input
            autoComplete="new-password"
            id="name"
            type="text"
            className={styles.form_input}
            required
            placeholder=" "
            value={form.name}
            onChange={(event) => setForm({...form, name: event.target.value})}
          />
          <label htmlFor="name" className={styles.form_label}>
            Наименование
          </label>
        </div>
        <div className={styles.input_container}>
          <select
            autoComplete="new-password"
            id="idFran"
            type="text"
            className={styles.form_input}
            required
            placeholder=" "
            value={form.idFran}
            onChange={(event) => setForm({...form, idFran: event.target.value})}
          >
            {franshisesIds.map(id => <Option idFran={id} form={form} setForm={setForm}/>)}
          </select>
          <label htmlFor="idFran" className={styles.form_label}>
            Франшиза
          </label>
        </div>
        <div className={styles.input_container}>
          <input
            autoComplete="new-password"
            id="orgOwner"
            type="text"
            className={styles.form_input}
            required
            placeholder=" "
            value={form.orgOwner}
            onChange={(event) => setForm({...form, orgOwner: event.target.value})}
          />
          <label htmlFor="orgOwner" className={styles.form_label}>
            Имя владельца
          </label>
        </div>
        <div className={styles.input_container}>
          <input
            autoComplete="new-password"
            id="phone"
            type="text"
            className={styles.form_input}
            required
            placeholder=" "
            value={form.phone}
            onChange={(event) => setForm({...form, phone: event.target.value})}
          />
          <label htmlFor="phone" className={styles.form_label}>
            Телефон
          </label>
        </div>
        <div className={styles.input_container}>
          <input
            autoComplete="new-password"
            id="worker"
            type="text"
            className={styles.form_input}
            required
            placeholder=" "
            value={form.worker}
            onChange={(event) => setForm({...form, worker: event.target.value})}
          />
          <label htmlFor="worker" className={styles.form_label}>
            Специалист
          </label>
        </div>
        <button
          type="submit"
          className={classNames(styles.button, styles.form_submit)}
        >
          Добавить ползователя
        </button>
      </form>
    </div>
  );
};
