import classNames from "classnames"
import { useState } from "react"
import { useDispatch } from "react-redux"
import { useNavigate } from "react-router-dom"
import { authSliceActions } from "../../store/Auth"
import logo from './images/logo.png'
import styles from './styles.module.css'

export const FormElement = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('')
    const [passwordVisible, setPasswordVisible] = useState(false)
    const navigate = useNavigate();
    const dispatch = useDispatch();
    const errorMessage = 'Неправильный пароль или логин!';
    let isValid = true;

    const handleValidate = () => {
        if (password.length === 0 || email.length === 0) {
            isValid = false
        }

        let lastAtPos = email.lastIndexOf("@");
        let lastDotPos = email.lastIndexOf(".");

        if (
            !(
              lastAtPos < lastDotPos &&
              lastAtPos > 0 &&
              email.indexOf("@@") == -1 &&
              lastDotPos > 2 &&
              email.length - lastDotPos > 2
            )
          ) {
            isValid = false;
          }
    }

    const handleSubmit = (event) => {
        event.preventDefault();
        handleValidate();
        if (isValid) {
            dispatch(authSliceActions.login({email}))
        }
        navigate('/cabinet');
    }

    const togglePasswordVisible = () => {
        setPasswordVisible(!passwordVisible);
    }
    
    return (
        <div className={styles.login_formWrapper}>
        <form onSubmit={handleSubmit} className={styles.form}>
            <img src={logo} alt='logo' className={styles.form_logo} />
            <div className={styles.input_container}>
                <input autoComplete="new-password" id="login" type="text" className={classNames(styles.form_input, {
                    [styles.form_input__noValid]: !isValid
                })} required placeholder=' ' value={email} onChange={(event) => setEmail(event.target.value)}/>
                <label htmlFor="login" className={styles.form_label}>Логин</label>
            </div>
            <div className={styles.input_container}>
                <input autoComplete="new-password" id="password" type={passwordVisible ? 'text' : 'password'} className={classNames(styles.form_input, {
                    [styles.form_input__noValid]: !isValid
                })} required placeholder=' ' value={password} onChange={(event) => setPassword(event.target.value)}/>
                <label htmlFor="password" className={styles.form_label}>Пароль</label><button className={classNames({[styles.form_hideButton]: passwordVisible,
                    [styles.form_hidePuttonFilled]: !passwordVisible})} type="button" onClick={togglePasswordVisible}></button>
            </div>
            <span className={styles.errorMessage}>{!isValid && errorMessage}</span>
            <button className={styles.form_submit} type="submit">Войти</button>
            <button className={styles.form_forget} type="button">Забыли пароль?</button>
        </form>
        </div>
    )
}
