import { useCallback } from "react";
import { useSelector } from "react-redux";
import { InputCountedOption } from "../../Components/InputCountedOption/InputCountedOption";
import { cartSliceActions } from "../../store/Cart";
import { selectCartOptionCount } from "../../store/Cart/selectors";

export const InputCountedContainer = ({ label, ...props }) => {
  const count = useSelector((state, { label }) =>
    selectCartOptionCount(state, { label })
  );

  const increment = useCallback(() => {
    cartSliceActions.addOption(label);
  }, [label]);
  const decrement = useCallback(() => {
    cartSliceActions.removeOption(label);
  }, [label]);

  return (
    <InputCountedOption
      increment={increment}
      decrement={decrement}
      value={count || 0}
      {...props}
    />
  );
};
