import { loadUsers } from "./loadUsersIfNotExist";

export const addUserIfValidate = ({userId, email, role, name, phone, city, pass}) => {
    
  const isDataValid = true;

  if (isDataValid) {
    const options = {
      method: "POST",
      headers: {
        "Content-Type": "application/json;charset=utf-8",
      },
      body: JSON.stringify({
        user: `insert into peoples(pid,name,email,phone,pass,idaccess) values(${userId},'${name}','${email}','${phone}','${pass}',${role});`,
      }),
    };

    fetch("http://localhost:4000/addUser", options)
      .then((res) => res.text())
      .then(() => loadUsers(localStorage.userId))
      .catch((err) => console.log(err));
  }
};
