import classNames from "classnames";
import { useState } from "react";
import { PACKAGES } from "../../constants/Fixtires";
import { InputCountedContainer } from "../../Containers/InputCountedContainer/InputCountedContainer";
import { FormElem } from "../FormElem/FormElem";
import { InputCountedOption } from "../InputCountedOption/InputCountedOption";
import { InputRadioPeriod } from "../InputRadioPeriod/InputRadioPeriod";
import { PackageOption } from "../PackageOption/PackageOptions";
import styles from "./styles.module.css";
import {InputPhone} from '../InputPhone/InputPhone'

export const EditPackageForm = ({ togglePopup }) => {
  const [form, setForm] = useState({
    stantion: 1,
    storage: 0,
    calculation: 0,
    tarifiation: 0,
  });

  const handleSubmit = (e) => {
    e.preventDefault();
  };

  return (
    <FormElem
      onSubmit={handleSubmit}
      title={"Смена тарифа"}>
      <div className={styles.options_container}>
        <InputCountedContainer
          label={"Станция"}
          required={true}
          value={form.stantion}
          setValue={(val) => {
            setForm({ ...form, stantion: val });
          }}
        />
        <InputCountedContainer
          label={"Склад"}
          value={form.storage}
          setValue={(val) => {
            setForm({ ...form, storage: val });
          }}
        />
        <InputCountedContainer
          label={"Калькуляция"}
          value={form.calculation}
          setValue={(val) => {
            setForm({ ...form, calculation: val });
          }}
        />
        <InputCountedContainer
          label={"Тарификация"}
          value={form.tarifiation}
          setValue={(val) => {
            setForm({ ...form, tarifiation: val });
          }}
        />
      </div>
      <div className={styles.period_container}>
        <span className={styles.subtitle}>Период оплаты</span>
        <InputRadioPeriod
          value={"1"}
          label={"Месяц"}
          setValue={console.log("1 month")}
        />
        <InputRadioPeriod
          value={"1"}
          label={"год"}
          setValue={console.log("1 month")}
        />
      </div>
      <div className={styles.option_container}>
      <PackageOption
        label={"Мобильный официант"}
        checked={true}
        setValue={console.log("Вы прекрасны!")}
      />
      </div>
      {true && <div className={styles.edit_mobile}>
        <InputPhone label={"Количество"} />
        </div>}
    </FormElem>
  );
};
