import { useCallback } from "react";
import { useDispatch, useSelector } from "react-redux";
import {
  InputCountedOption,
  MemoInputCountedOption,
} from "../../Components/InputCountedOption/InputCountedOption";
import { cartSliceActions } from "../../store/Cart";
import { selectCartOptionCount } from "../../store/Cart/selectors";

export const InputCountedContainer = ({ label, ...props }) => {
  const dispatch = useDispatch();
  const count = useSelector((state) => selectCartOptionCount(state, { label }));

  const increment = useCallback(
    () => dispatch(cartSliceActions.addOption(label)),
    [label]
  );
  const decrement = useCallback(
    () => dispatch(cartSliceActions.removeOption(label)),
    [label]
  );

  return (
    <MemoInputCountedOption
      increment={increment}
      decrement={decrement}
      value={count || 0}
      label={label}
      {...props}
    />
  );
};
