import classNames from "classnames";
import { useCallback } from "react";
import styles from "./styles.module.css";

export const InputCountedOption = ({ required, label, value, setValue }) => {
  const increment = useCallback(() => {
    setValue(value + 1);
  }, [value]);
  const decrement = useCallback(() => {
    setValue(value - 1 !== (required ? 1 : 0) ? value - 1 : 0);
  }, [value]);

  return (
    <div
      className={classNames(styles.input_container, {
        [styles.input_requered]: required,
        [styles.input_active]: value !== 0,
      })}>
      <h4 className={styles.title}>{label}</h4>
      <div className={styles.count_container}>
        <button
          type="button"
          onClick={decrement}
          className={classNames(styles.button, styles.decrement)}
        />
        <input
          type="number"
          value={value}
          className={styles.count_input}
          id={label} 
          onChange={(e) => setValue(e.target.value)}/>
        <button
          type="button"
          onClick={increment}
          className={classNames(styles.button, styles.increment)}
        />
      </div>
    </div>
  );
};
