import classNames from "classnames";
import { useState } from "react";
import styles from "./styles.module.css";
const packs = {
  start: "Start",
  max: "Max",
  base: "Base",
};

export const EditPackagePopup = ({ togglePopup }) => {
  const [pack, setPack] = useState(packs.start);
  const [porion, setPerion] = useState(1);
  return (
    <div className={styles.popup_wrapper}>
      <div
        className={classNames(styles.popup, {
          [styles.popup__base]: pack === packs.base,
          [styles.popup__max]: pack === packs.max,
        })}
      >
        <button
          className={classNames(styles.button, styles.popup_closeButton)}
          type="button"
          onClick={() => togglePopup()}
        />
        <div
          className={classNames(styles.radio_container, {
            [styles.radio_container__aective]: pack === packs.start,
          })}
          onClick={() => setPack(packs.start)}
        >
          {packs.start}
        </div>
        <div
          className={classNames(styles.radio_container, {
            [styles.radio_container__aective]: pack === packs.base,
          })}
          onClick={() => setPack(packs.base)}
        >
          {packs.base}
        </div>
        <div
          className={classNames(styles.radio_container, {
            [styles.radio_container__aective]: pack === packs.max,
          })}
          onClick={() => setPack(packs.max)}
        >
          {packs.max}
        </div>
        <div className={styles.container}>
          <div className={styles.date_container}>
            <input type="date" id="dateStart" className={styles.date}></input>
            <label htmlFor="dateStart">Дата начала</label>
          </div>
          <div className={styles.date_container}>
            <input type="number" id="office" className={styles.count}></input>
            <label htmlFor="office" className={styles.count_label}>
              Количество Front-office
            </label>
          </div>
        </div>
        <div className={styles.period_container}>
          <label htmlFor="1 month">
            <input type="radio" onChange={() => setPerion(1)}>
            </input>
          </label>
          <label htmlFor="3 month">
            <input type="radio" id="3 month" onChange={() => setPerion(3)}>
            </input>
          </label>
          <label htmlFor="12 month">
            <input type="radio" id='12 month' onChange={() => setPerion(12)}>
            </input>
          </label>
          <label htmlFor="24 month">
            <input type="radio" id='24 month' onChange={() => setPerion(24)}>
            </input>
          </label>
          <label htmlFor="36 month">
            <input type="radio" id="36 month" onChange={() => setPerion(36)}>
            </input>
          </label>
        </div>
      </div>
    </div>
  );
};
