import { useSelector } from "react-redux";
import { UserData } from "../Components/UserData/UserData";
import { selectUserDataById } from "../store/Users/selectors";
import styles from "./styles.module.css";
import classNames from "classnames";
import { selectObjectById } from "../store/Objects/selectors";
import { getDiffDates } from "../Components/UserData/helpers/getDiffDates";

export const UserDataContainer = ({ onclick, id, filter, selectedRow }) => {
  const user = useSelector((state) => selectUserDataById(state, { id }));
  const data = [
    user.ID,
    user.NAME,
    user.EMAIL,
    user.PHONE,
    user.ROLENAME,
    "FRAN_NAME",
    user.BALANCE
      ? user.BALANCE.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ",")
      : 0,
  ];

  const object = useSelector((state) => selectObjectById(state, { id }));
  const diffDates =
    object && object.STARTDT && object.ENDDT
      ? getDiffDates({ start: object.STARTDT, end: object.ENDDT })
      : null;

  const getStyleByLeftMonths = () => {
    if (!diffDates) {
      return styles.table_cell__num
    }
    if (diffDates < 1) {
      return styles.red
    }
    if (diffDates === 1) {
      return styles.yellow
    }
    if (diffDates > 1) {
        return styles.green
    }
  };
  

  return (
    <tr
      className={classNames(styles.table_row, {
        [styles.table_row__selected]: selectedRow ? selectedRow === id : false,
        [styles.yellow]: true,
      })}
      onClick={() => onclick(id)}
    >
      <UserData data={data} selectedRow={selectedRow} />
    </tr>
  );
};
