import classNames from "classnames";
import { useState } from "react";
import { PACKAGES } from "../../constants/Fixtires";
import { InputRadioPeriod } from "../InputRadioPeriod/InputRadioPeriod";
import styles from "./styles.module.css";


export const EditPackageForm = ({ togglePopup }) => {
  const [form, setForm] = useState({
    pack: PACKAGES.start,
    packagePeriod: undefined,
    officePeriod: undefined,
    office: false,
    packageDate: undefined,
    qr: false,
    frontOffice: undefined,
    tarification: false,
    mobile: undefined,
    officeCount: undefined,
  });

  const handleSubmit = (e) => {
    e.preventDefault();
  };

  return (
    <>
      <div className={styles.radios_container}>
        <div
          className={classNames(styles.radio_container, {
            [styles.radio_container__aective]: form.pack === PACKAGES.start,
          })}
          onClick={() => setForm({ ...form, pack: PACKAGES.start })}>
          {PACKAGES.start}
        </div>
        <div
          className={classNames(styles.radio_container, {
            [styles.radio_container__aective]: form.pack === PACKAGES.base,
          })}
          onClick={() => setForm({ ...form, pack: PACKAGES.base })}>
          {PACKAGES.base}
        </div>
        <div
          className={classNames(styles.radio_container, {
            [styles.radio_container__aective]: form.pack === PACKAGES.max,
          })}
          onClick={() => setForm({ ...form, pack: PACKAGES.max })}>
          {PACKAGES.max}
        </div>
      </div>
      <div
        className={classNames(styles.popup, {
          [styles.popup__base]: form.pack === PACKAGES.base,
          [styles.popup__max]: form.pack === PACKAGES.max,
        })}>
        <div className={styles.container}>
          <div className={styles.date_container}>
            <input
              type="date"
              id="dateStart"
              value={form.packageDate}
              onChange={(e) =>
                setForm({ ...form, packageDate: e.target.value })
              }
              className={styles.date}></input>
            <label
              htmlFor="dateStart"
              className={styles.label}>
              Дата начала
            </label>
          </div>
          <div className={styles.date_container}>
            <input
              type="number"
              id="office"
              value={form.frontOffice}
              onChange={(e) =>
                setForm({ ...form, frontOffice: e.target.value })
              }
              className={styles.count}></input>
            <label
              htmlFor="office"
              className={classNames(styles.count_label, styles.label)}>
              Количество Front-office
            </label>
          </div>
        </div>
        <div className={styles.period_container}>
          <h3 className={styles.period_title}>Период</h3>
          <InputRadioPeriod
            label={"1 месяц"}
            active={form.packagePeriod === 1}
            setValue={() => setForm({ ...form, packagePeriod: 1 })}
          />
          <InputRadioPeriod
            label={"3 месяца"}
            active={form.packagePeriod === 3}
            setValue={() => setForm({ ...form, packagePeriod: 3 })}
          />
          <InputRadioPeriod
            label={"1год"}
            active={form.packagePeriod === 12}
            setValue={() => setForm({ ...form, packagePeriod: 12 })}
          />
          <InputRadioPeriod
            label={"2 года"}
            active={form.packagePeriod === 24}
            setValue={() => setForm({ ...form, packagePeriod: 24 })}
          />
          <InputRadioPeriod
            label={"3 года"}
            active={form.packagePeriod === 36}
            setValue={() => setForm({ ...form, packagePeriod: 36 })}
          />
        </div>
        <label
          htmlFor="switchTar"
          className={classNames(styles.switch, {
            [styles.switch__active]: form.tarification,
          })}>
          Тарификация
          <input
            type="checkbox"
            onChange={(e) =>
              setForm({ ...form, tarification: !form.tarification })
            }
            checked={form.tarification}
            id="switchTar"
            className={styles.toggle}></input>
        </label>
        <label
          htmlFor="switchQr"
          className={classNames(styles.switch, {
            [styles.switch__active]: form.qr,
          })}>
          QR Меню
          <input
            type="checkbox"
            onChange={(e) => setForm({ ...form, qr: !form.qr })}
            checked={form.qr}
            id="switchQr"
            className={styles.toggle}></input>
        </label>
        <label
          htmlFor="switch"
          className={classNames(styles.switch, {
            [styles.switch__active]: form.office,
          })}>
          Мобильный официант
          <input
            type="checkbox"
            onChange={(e) => setForm({ ...form, office: !form.office })}
            checked={form.office}
            id="switch"
            className={styles.toggle}></input>
        </label>
        {form.office && (
          <>
            <div className={styles.container}>
              <div className={styles.date_container}>
                <input
                  type="date"
                  id="dateStart"
                  className={styles.date}
                  value={form.mobile}
                  onChange={(e) =>
                    setForm({ ...form, mobile: e.target.value })
                  }></input>
                <label
                  htmlFor="dateStart"
                  className={styles.label}>
                  Дата начала
                </label>
              </div>
              <div className={styles.date_container}>
                <input
                  type="number"
                  id="office"
                  className={styles.count}
                  value={form.officeCount}
                  onChange={(e) =>
                    setForm({
                      ...form,
                      officeCount: e.target.value >= 0 ? e.target.value : 0,
                    })
                  }></input>
                <label
                  htmlFor="office"
                  className={classNames(styles.count_label, styles.label)}>
                  Количество
                </label>
              </div>
            </div>
            <div className={styles.period_container}>
              <button
                type="button"
                className={classNames(styles.perion_button, {
                  [styles.period_button__active]: form.officePeriod === 1,
                })}
                onClick={() => setForm({ ...form, officePeriod: 1 })}>
                1 Месяц
              </button>
              <button
                type="button"
                className={classNames(styles.perion_button, {
                  [styles.period_button__active]: form.officePeriod === 3,
                })}
                onClick={() => setForm({ ...form, officePeriod: 3 })}>
                3 Месяца
              </button>
              <button
                type="button"
                className={classNames(styles.perion_button, {
                  [styles.period_button__active]: form.officePeriod === 12,
                })}
                onClick={() => setForm({ ...form, officePeriod: 12 })}>
                1 Год
              </button>
              <button
                type="button"
                className={classNames(styles.perion_button, {
                  [styles.period_button__active]: form.officePeriod === 24,
                })}
                onClick={() => setForm({ ...form, officePeriod: 24 })}>
                2 Года
              </button>
              <button
                type="button"
                className={classNames(styles.perion_button, {
                  [styles.period_button__active]: form.officePeriod === 36,
                })}
                onClick={() => setForm({ ...form, officePeriod: 36 })}>
                3 Года
              </button>
            </div>
          </>
        )}

        <div
          className={classNames(styles.amount, {
            [styles.amount__base]: form.pack === PACKAGES.base,
            [styles.amount__max]: form.pack === PACKAGES.max,
          })}>
          120,000
          <button
            type="submit"
            onClick={(e) => handleSubmit(e)}
            className={styles.submit}></button>
        </div>
      </div>
    </>
  );
};
