import classNames from "classnames";
import styles from "./styles.module.css";
import { nanoid } from "nanoid";
import { AddPopup } from "../AddPopup/AddPopup";
import { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useEffect } from "react";
import {
  selectUsers,
  selectUsersIds,
  selectUsersIsLoading,
} from "../../store/Users/selectors";
import { loadUsers } from "../../store/Users/Thunks/loadUsers";
import { DeletePopup } from "../DeletePopup/DeletePopup";
import { EditPopup } from "../EditPopup/EditPopup";
import { ButtonBar } from "../ButtonsBar/ButtonsBar";
import { UserData } from "../UserData/UserData";
const userStyles = {
  cell: styles.table_cell,
  cell_num: classNames(styles.table_cell, styles.table_cell__num),
  row: styles.table_row,
  cell__balance: styles.table_cell__balance,
  cell_num_red: classNames(styles.table_cell, styles.table_cell__num, styles.red),
  cell_num_yellow: classNames(styles.table_cell, styles.table_cell__num, styles.yellow),
  cell_num_green: classNames(styles.table_cell, styles.table_cell__num, styles.green)
};

export const Users = ({ asideIsOpened }) => {        
  const barStyles = {
  button: classNames(styles.bar_button, styles.button),
  delete: classNames(
    styles.bar_button,
    styles.bar_button__delete,
    styles.button,
  ),
  img: styles.bar_buttonImage,
  wrapper: classNames(styles.bar_container, {
    [styles.bar_moved]: !asideIsOpened,
  })
}
  const dispatch = useDispatch();
  const isLoading = useSelector((state) => selectUsersIsLoading(state));
  useEffect(() => {
      dispatch(loadUsers({userId}));
    setIsRowSelected(false)
  }, []);
  const usersIds = useSelector((state) => selectUsersIds(state));
  const usersData = useSelector((state) => selectUsers(state));
  const [isPopupOpened, setIsPopupOpened] = useState(false);
  const [isRowSelected, setIsRowSelected] = useState(false);
  const [isEditPopupOpened, setIsEditPopupOpened] = useState(false);
  const [isDeletePopupOpened, setIsDeletePopupOpened] = useState(false);
  const [selectedRow, setSelectedRow] = useState(false);
  
  const userId = localStorage.userId/1;
  
  const toggleAddPopup = () => {
    setIsPopupOpened(!isPopupOpened);
  };
  const resetSelectedRow = () => {
    setSelectedRow(false);
    setIsRowSelected(false);
  };
  const toggleDeletePopup = () => {
    setIsDeletePopupOpened(!isDeletePopupOpened);
  };

  const toggleEditPopup = () => {
    setIsEditPopupOpened(!isEditPopupOpened);
  };

  const toggleSelectedRow = (el) => {
    if (userId/1 !== el) {
      if (!!el) {
        if (selectedRow === el) {
          resetSelectedRow();
        } else {
          setIsRowSelected(true);
          setSelectedRow(el);
        }
      } else {
        resetSelectedRow();
      }
    }
  };

  const headers = [
    "#",
    "НАИМЕНОВАНИЕ",
    "E-mail",
    "Телефон",
    "Роль",
    "Франшиза",
    "Баланс",
  ];

  if (isLoading) {
    return <div>Loading...</div>;
  }

  return (
    <div className={styles.users_wrapper}>
      {usersData ? (
        <table className={styles.table}>
          <thead className={styles.table_header}>
            <tr className={styles.table_row}>
              {headers?.map((el) => {
                return (
                  <th
                    key={nanoid()}
                    className={classNames(
                      styles.table_cell,
                      styles.table_cell__header
                    )}
                  >
                    {el}
                  </th>
                );
              })}
            </tr>
          </thead>
          <tbody className={styles.table_content}>
            {usersIds?.map((el, row) => {
              const user = usersData[el];
              const { ID, BALANCE, CITY, EMAIL, NAME, PHONE, ROLENAME } = user;
              <UserData styles={userStyles} onclick={toggleSelectedRow} userId={el}/>

              // return (
              //   <tr
              //     key={el}
              //     className={classNames(styles.table_row, {
              //       [styles.table_row__selected]: selectedRow === el,
              //     })}
              //     onClick={() => toggleSelectedRow(el)}
              //   >
              //     {headers.map((_, idx) => {
              //       switch (idx) {
              //         default:
              //           return (
              //             <td key={nanoid()} className={styles.table_cell}></td>
              //           );
              //         case 0:
              //           return (
              //             <td
              //               key={nanoid()}
              //               className={classNames(
              //                 styles.table_cell,
              //                 styles.table_cell__num
              //               )}
              //             >
              //               {ID}
              //             </td>
              //           );
              //         case 1:
              //           return (
              //             <td key={nanoid()} className={styles.table_cell}>
              //               {NAME}
              //             </td>
              //           );
              //         case 2:
              //           return (
              //             <td key={nanoid()} className={styles.table_cell}>
              //               {EMAIL}
              //             </td>
              //           );
              //         case 3:
              //           return (
              //             <td key={nanoid()} className={styles.table_cell}>
              //               {PHONE}
              //             </td>
              //           );
              //         case 4:
              //           return (
              //             <td key={nanoid()} className={styles.table_cell}>
              //               {ROLENAME}
              //             </td>
              //           );
              //         case 5:
              //           return (
              //             <td key={nanoid()} className={styles.table_cell}>
              //               {CITY}
              //             </td>
              //           );
              //         case 6:
              //           return (
              //             <td
              //               key={nanoid()}
              //               className={classNames(
              //                 styles.table_cell,
              //                 styles.table_cell__num
              //               )}
              //             >
              //               <div className={styles.table_cell__balance}>
              //                 {BALANCE
              //                   ? BALANCE.toString().replace(
              //                       /\B(?=(\d{3})+(?!\d))/g,
              //                       ","
              //                     )
              //                   : 0}
              //               </div>
              //             </td>
              //           );
              //       }
              //     })}
              //   </tr>
              // );
            })}
          </tbody>
        </table>
      ) : (
        <div className={styles.emptyData}>Данные отсутствуют</div>
      )}

      <ButtonBar onClicks={[toggleAddPopup, toggleEditPopup, toggleDeletePopup]} disabled={!isRowSelected} styles={barStyles} />
      {isPopupOpened && <AddPopup toggleAddPopup={toggleAddPopup} />}
      {isDeletePopupOpened && (
        <DeletePopup
          id={selectedRow}
          toggleDeletePopup={toggleDeletePopup}
          resetSelectedRow={resetSelectedRow}
        />
      )}
      {isEditPopupOpened && (
        <EditPopup
          id={selectedRow}
          togglePopup={toggleEditPopup}
          resetSelectedRow={resetSelectedRow}
        />
      )}
    </div>
  );
};
