import classNames from "classnames";
import { useState } from "react";
import { PACKAGES } from "../../constants/Fixtires";
import { InputCountedContainer } from "../../Containers/InputCountedContainer/InputCountedContainer";
import { FormElem } from "../FormElem/FormElem";
import { InputCountedOption } from "../InputCountedOption/InputCountedOption";
import { InputRadioPeriod } from "../InputRadioPeriod/InputRadioPeriod";
import { PackageOption } from "../PackageOption/PackageOptions";
import styles from "./styles.module.css";
import {InputPhone} from '../InputPhone/InputPhone'

export const EditPackageForm = ({ togglePopup }) => {
  const [form, setForm] = useState({
    stantion: 1,
    storage: 0,
    calculation: 0,
    tarifiation: 0,
  });

  const handleSubmit = (e) => {
    e.preventDefault();
  };

  return (
    <FormElem
      onSubmit={handleSubmit}
      title={"Смена тарифа"}>
      <div className={styles.options_container}>
        <InputCountedContainer
          label={"Станция"}
          required={true}
          value={form.stantion}
          setValue={(val) => {
            setForm({ ...form, stantion: val });
          }}
        />
        <InputCountedContainer
          label={"Склад"}
          value={form.storage}
          setValue={(val) => {
            setForm({ ...form, storage: val });
          }}
        />
        <InputCountedContainer
          label={"Калькуляция"}
          value={0}
          setValue={(val) => {
            setForm({ ...form, calculation: val });
          }}
        />
        <InputCountedContainer
          label={"Тарификация"}
          value={form.tarifiation}
          setValue={(val) => {
            setForm({ ...form, tarifiation: val });
          }}
        />
        <div className={styles.exestintion_container} >
        <InputCountedContainer
          label={"Моби официант"}
          value={form.tarifiation}
          setValue={(val) => {
            setForm({ ...form, tarifiation: val });
          }}
        />
        <div className={styles.exestintion}>
        <h4 className={styles.subtitle}>Период оплаты</h4>
        <button className={classNames(styles.button_period, {
          [styles.button_period__active]: true,
        })}>Month</button>
        <button className={classNames(styles.button_period, {
          [styles.button_period__active]: false,
        })}>Year</button>
        </div>
        </div>
      </div>
    </FormElem>
  );
};
