import { createSlice } from "@reduxjs/toolkit";

const initialState = {
  entities: {},
  ids: [],
  status: "idle",
};

export const usersSlice = createSlice({
  name: "users",
  initialState,
  reducers: {
    startLoading: () => {
      return {
        entities: {},
        ids: [],
        status: "loading",
      };
    },
    successLoading: (state, action) => {
      const {entities, ids} = action.payload;
      return {
        entities,
        ids,
        status: "success"
      }
    },
    failLoading: () => {
      return {
        entities: {},
        ids: [],
        status: "fail",
      };
    },
    addUser: (state, action) => {
        const {email, name, phone, city, password, role} = action.payload;

        state.email = {
            email,
            name,
            phone,
            city,
            password,
            role
        }
    }
  },
});

export const usersSliceActions = usersSlice.actions
