import { Children } from "react";
import { OBJECT_HEADERS } from "../../constants/Fixtires";
import { ObjectDataContainer } from "../../Containers/ObjectDataContainer/ObjectDataContainer";
import { SearchBar } from "../SearchBar/SearchBar";
import styles from './styles.module.css'


export const Table = ({headers, children}) => {
    return (
        <table className={styles.table}>
            <thead className={styles.table_header}>
              <tr className={styles.table_row}>
                {headers.map((el) => {
                  return (
                    <th
                      key={nanoid()}
                      className={classNames(
                        styles.table_cell,
                        styles.table_cell__header
                      )}>
                      {el === "Название" ? (
                        <SearchBar
                          filter={filter.name}
                          handleSearch={setName}
                          placeholder={el}
                        />
                      ) : (
                        el
                      )}
                    </th>
                  );
                })}
              </tr>
            </thead>
            <tbody className={styles.table_content}>
              {objectsIds?.map((id) => (
                <ObjectDataContainer
                  key={id}
                  id={id}
                  filter={filter}
                  onclick={setIsRowSelected}
                />
              ))}
            </tbody>
          </table>
    )
}
