import classNames from "classnames";
import { useEffect, useState } from "react";
import { useDispatch } from "react-redux";
import { addUser } from "../../store/Users/Thunks/addUser";
import { useValidate } from "./hooks/useValidate";
import styles from "./styles.module.css";
const roleVars = {
  admin: { content: "admin", id: 1 },
  user: { content: "user", id: 3 },
  master: { content: "master", id: 2 },
};

export const AddPopup = ({ toggleAddPopup }) => {
  const dispatch = useDispatch();
  const [email, setEmail] = useState("");
  const [name, setName] = useState("");
  const [phone, setPhone] = useState("");
  const [city, setCity] = useState("");
  const [pass, setPass] = useState("");
  const [role, setRole] = useState(roleVars.user.id);
  const handleValidate = useValidate()
  const [form, setForm] = useState({
    email: null,
    name: null,
    phone: null,
    city: null,
    pass: null,
    role: roleVars.user.id,
  })
  const initialValidate = {
    validate: undefined,
    errorMessage: ' ',
  }
  const [validate, setValidate] = useState(initialValidate);
  useEffect(() => {
    if (validate.errorMessage.length < 5 && phone.length > 8) {
      dispatch(addUser({form}));
      toggleAddPopup();
      resetForm();
      console.log('user added')
    }
  }, [validate.errorMessage]);
  const userId = localStorage.userId;

  const resetForm = () => {
    setCity("");
    setEmail("");
    setName("");
    setPass("");
    setPhone("");
    setRole(roleVars.user.id);
  };


  const onSubmit = (event) => {
    event.preventDefault();
    handleValidate({setValidate, form});
  };

  return (
    <div className={styles.popup_wrapper}>
      <form className={styles.form} onSubmit={onSubmit}>
        <h2 className={styles.title}>Добавление пользователя</h2>
        <button
          type="button"
          className={classNames(styles.button, styles.popup_closeButton)}
          onClick={() => toggleAddPopup()}
        />
        <div className={styles.input_container}>
          <input
            autoComplete="new-password"
            id="name"
            type="text"
            className={styles.form_input}
            placeholder=" "
            value={name}
            onChange={(event) => setForm({...form, name: event.target.value})}
          />
          <label htmlFor="name" className={styles.form_label}>
            Наименование
          </label>
        </div>
        <div className={styles.input_container}>
          <input
            autoComplete="new-password"
            id="email"
            type="text"
            className={styles.form_input}
            placeholder=" "
            value={email}
            onChange={(event) => setForm({...form, email: event.target.value})}
          />
          <label htmlFor="email" className={styles.form_label}>
            Email
          </label>
        </div>
        <div className={styles.input_container}>
          <input
            autoComplete="new-password"
            id="phone"
            type="text"
            className={styles.form_input}
            placeholder=" "
            value={phone}
            onChange={(event) => setForm({...form, phone: event.target.value})}
          />
          <label htmlFor="phone" className={styles.form_label}>
            Телефон
          </label>
        </div>
        <div className={styles.input_container}>
          <input
            autoComplete="new-password"
            id="city"
            type="text"
            className={styles.form_input}
            placeholder=" "
            value={city}
            onChange={(event) => setForm({...form, city: event.target.value})}
          />
          <label htmlFor="city" className={styles.form_label}>
            Город
          </label>
        </div>
        <div className={styles.input_container}>
          <input
            autoComplete="new-password"
            id="password"
            type="password"
            className={styles.form_input}
            placeholder=" "
            value={pass}
            onChange={(event) => setForm({...form, pass: event.target.value})}
          />
          <label htmlFor="password" className={styles.form_label}>
            Пароль
          </label>
        </div>
        <span className={styles.radios_label}>Выберите роль:</span>
        <div className={styles.radios_container}>
          <div className={styles.radio_container}>
            <input
              className={styles.radio}
              type="radio"
              value={roleVars.user.content}
              onChange={() => setForm({...form, role: roleVars.user.id})}
              checked={role === roleVars.user.id}
            />
            {roleVars.user.content}
            <label className={styles.label_radio}></label>
          </div>
          <div className={styles.radio_container}>
            <input
              className={styles.radio}
              type="radio"
              value={roleVars.master.content}
              onChange={() => setForm({...form, role: roleVars.master.id})}
              checked={role === roleVars.master.id}
            />
            {roleVars.master.content}
            <label className={styles.label_radio}></label>
          </div>
          <div className={styles.radio_container}>
            <input
              className={styles.radio}
              type="radio"
              value={roleVars.admin.content}
              onChange={() => setForm({...form, role: roleVars.admin.id})}
              checked={role === roleVars.admin.id}
            />
            {roleVars.admin.content}
            <label className={styles.label_radio}></label>
          </div>
        </div>
        <span className={styles.errorMessage}>{validate.errorMessage}</span>
        <button
          type="submit"
          className={classNames(styles.button, styles.form_submit)}
        >
          Добавить ползователя
        </button>
      </form>
    </div>
  );
};
