import classNames from "classnames";
import { useState } from "react";
import { PACKAGES } from "../../constants/Fixtires";
import { FormElem } from "../FormElem/FormElem";
import { InputCountedOption } from "../InputCountedOption/InputCountedOption";
import { InputRadioPeriod } from "../InputRadioPeriod/InputRadioPeriod";
import { PackageOption } from "../PackageOption/PackageOptions";
import styles from "./styles.module.css";

export const EditPackageForm = ({ togglePopup }) => {
  const [form, setForm] = useState({
    stantion: 1,
  });

  const handleSubmit = (e) => {
    e.preventDefault();
  };

  return (
    <FormElem
      onSubmit={handleSubmit}
      title={"Смена тарифа"}>
      <div className={styles.options_container}>
        <InputCountedOption
          label={"Станция"}
          requered={true}
          value={form.stantion}
          setValue={(val) => {
            setForm({ ...form, stantion: val });
          }}
        />
      </div>
    </FormElem>
  );
};
