import { useCallback } from "react";
import { useDispatch, useSelector } from "react-redux";
import { InputCountedOption } from "../../Components/InputCountedOption/InputCountedOption";
import { cartSliceActions } from "../../store/Cart";

export const InputCountedContainer = ({ label, ...props }) => {
  const dispatch = useDispatch();

  const increment = useCallback(
    () => dispatch(cartSliceActions.addOption(label)),
    [label]
  );
  const decrement = useCallback(
    () => dispatch(cartSliceActions.removeOption(label)),
    [label]
  );

  return (
    <InputCountedOption
      increment={increment}
      decrement={decrement}
      label={label}
      {...props}
    />
  );
};
