import { useSelector } from "react-redux";
import { selectAuthUser } from "../../store/Auth/selectors";
import styles from "./styles.module.css";
import classNames from "classnames";
import { useState } from "react";
import usersIMG from './images/person.svg';
import objectIMG from './images/domain.svg';
import profileIMG from './images/Users.svg';
import { Users } from "../../Components/Users/Users";
import { Profile } from "../../Components/Profile/Profile";
import { Objects } from "../../Components/Objects/Objects";

const FOLDERS = {
  users: 'users',
  profile: 'profile',
  objects: 'objects',
}

export const Cabinet = () => {
  const user = useSelector((state) => selectAuthUser(state));
  const [activeFolder, setActiveFolder] = useState(FOLDERS.users)

  return (
    <div className={styles.cabinet}>
      <header className={styles.header}>
        <div className={styles.header_logoWrapper}>
          <a
            href="#"
            className={classNames(styles.link, styles.header_link)}>
          </a>
        </div>
        <button
          type="button"
          className={classNames(styles.button, styles.header_button)}></button>
      </header>
      <aside className={styles.navWrapper}>
        <div className={styles.user}>
          <img
            className={styles.user_avatar}
            alt=" "
            src={null}
          />
          <div className={styles.user_info}>
            <div className={styles.user_name}>
              {user ? user.firstName + ' ' + user.lastName : 'Ivan Ivanov'}
            </div>
            <div className={styles.user_from}>{user.company}</div>
          </div>
        </div>
        <nav className={styles.tabs_container}>
          <button
            type="button"
            className={classNames(
              styles.tab,
              styles.button, {
                [styles.tab_active]: activeFolder === FOLDERS.users
              }
            )}
            onClick={() => setActiveFolder(FOLDERS.users)}>
            <img
                src={profileIMG}
                alt=""
              className={styles.tab_icon}></img>
            <div className={styles.tab_text}>Пользователи</div>
          </button>
          <button
            type="button"
            className={classNames(styles.tab, styles.button, {
              [styles.tab_active]: activeFolder === FOLDERS.profile
            })}
            onClick={() => setActiveFolder(FOLDERS.profile)}>
            <img
                src={usersIMG}
              className={styles.tab_icon}></img>
            <div className={classNames(styles.button)}>Профиль</div>
          </button>
          <button
            type="button"
            className={classNames(styles.tab, styles.button, {
              [styles.tab_active]: activeFolder === FOLDERS.objects
            })}
            onClick={() => setActiveFolder(FOLDERS.objects)}>
            <img
                src={objectIMG}
                alt=""
              className={styles.tab_icon}></img>
            <div className={classNames(styles.button)}>Объекты</div>
          </button>
        </nav>
        <button
          type="button"
          className={classNames(styles.button, styles.exitButton)}>
          Выйти
        </button>
      </aside>
      <section className={styles.content}>
        {activeFolder === FOLDERS.users && <Users />}
        {activeFolder === FOLDERS.profile && <Profile />}
        {activeFolder === FOLDERS.objects && <Objects />}
      </section>
    </div>
  );
};
