import classNames from 'classnames'
import styles from './styles.module.css'
import { nanoid } from 'nanoid'
import addIMG from'./images/add.svg'
import editIMG from'./images/edit.svg'
import deleteIMG from "./images/delete.svg"

export const Users = () => {
    const data = [];

    for (let i=0; i< 50; i++) {
        data.push({'Наименование': 'UM System Group',
        "E-mail": 'example@mail.ru',
        "Телефон": '+77007007070',
        "Роль": 'admin',
        "Франшиза": 'Шымкент, Сарыагаш',
        "баланс": '250000'})
    }

    const headers = ['#'].concat(Object.keys(data[0]))
    
    return (
        data
        ? <div className={styles.table}>
            <div className={styles.table_header}>
                {headers.map(el => {
                    return (
                        <div key={nanoid()} className={classNames(styles.table_cell, styles.table_title)}>{el.toUpperCase()}</div>
                    )
                })}
            </div>
            <div className={styles.table_content}>
                {data.map((el, index) => {
                    const cellsValues = Object.values(el);

                    return (
                        <div key={nanoid()} className={styles.table_row}>
                            <div className={classNames(styles.table_cell, styles.table_cell_count)}>{index+1}</div>
                            {cellsValues.map((cell, idx) => {
                                if (idx===cellsValues.length-1) {
                                    const balance = cell.replace(/\B(?=(\d{3})+(?!\d))/g, ",")
                                    return <div key={nanoid()} className={styles.table_cell}><div className={styles.table_cell__balance}>{balance}</div></div>
                                }
                                return <div key={nanoid()} className={styles.table_cell}>{cell}</div>
                            })}
                        </div>
                    )
                })}
            </div>
            <div className={styles.bar_container}>
                <button className={classNames(styles.button, styles.bar_button, styles.bar_button__add)} onClick={
                    () => console.log('add')
                }><img src={addIMG} alt="" className={styles.bar_buttonImage}/></button>
                <button className={classNames(styles.button, styles.bar_button, styles.bar_button__edit)} onClick={
                    () => console.log('edit')
                }><img src={editIMG} alt=""/></button>
                <button className={classNames(styles.button, styles.bar_button, styles.bar_button__delete)} onClick={
                    () => console.log('delete')
                }><img src={deleteIMG} alt=""/></button>
            </div>
        </div>
        : <div>no data</div>
    )
}
