import { createSlice } from '@reduxjs/toolkit';

const initialState = {
  entities: {},
  ids: [],
  status: 'idle',
};

export const usersSlice = createSlice({
  name: 'users',
  initialState,
  reducers: {
    startLoading: () => {
      return {
        entities: {},
        ids: [],
        status: 'loading',
      };
    },
    successLoading: (state, action) => {
      const { entities, ids } = action.payload;
      return {
        entities,
        ids,
        status: 'success',
      };
    },
    failLoading: () => {
      return {
        entities: {},
        ids: [],
        status: 'fail',
      };
    },
    addUser: (state, action) => {
    console.log(action.payload)
      const { email, name, phone, city, password, role } = action.payload;

      state.email = {
        'Наименование': name,
        'E-mail': email,
        'Телефон': phone,
        'Роль': role,
        'Франшиза': 'Шымкент, Сарыагаш',
        'баланс': 0,
      };

      return state
    },
  },
});

export const usersSliceActions = usersSlice.actions;

