import { useState } from "react";
import { setNewPassword } from "../../store/Auth/Thunks/setNewPassword";
import styles from "./styles.module.css";
import { useDispatch } from "react-redux";
import { useEffect } from "react";

export const Profile = () => {
  const dispatch = useDispatch();
  const initialValidate = {
    validate: true,
    errorMessage: false,
  }
  const [validate, setValidate] = useState(initialValidate);
  useEffect(() => {
    if (validate.validate) {
      dispatch(
        setNewPassword({ pass: form.newPassword, id: localStorage.userId / 1 })
      );
    }
  }, [validate.validate]);

  const [form, setForm] = useState({
    password: "",
    newPassword: "",
    repeat: "",
  });

  const handleValidate = () => {
    const regularExpression =
      /^(?=.*[0-9])(?=.*[!@#$%^&*])[a-zA-Z0-9!@#$%^&*]{6,16}$/;
    if (form.newPassword !== form.repeat) {
      setValidate({ validate: false, errorMessage: "Пароли не совпадают!" });
    }

    if (!regularExpression.test(form.newPassword)) {
      setValidate({
        validate: false,
        errorMessage:
          "Новый пароль должен содержать латинские буквы верзнего и нижнего регистра, цифры и специальные символы и быть длинной от 6 до 16 символов",
      });
    }

    return validate.validate;
  };

  const onSubmit = (e) => {
    e.preventDefault();
    handleValidate()
  };

  return (
    <div className={styles.profile}>
      <h2 className={styles.title}>Сбросить пароль </h2>
      <div className={styles.input_container}>
        <input
          autoComplete="new-password"
          id="old"
          type="password"
          className={styles.form_input}
          required
          placeholder=" "
          value={form.password}
          onChange={(event) =>
            setForm({ ...form, password: event.target.value })
          }
        />
        <label htmlFor="old" className={styles.form_label}>
          Старый пароль
        </label>
      </div>
      <div className={styles.input_container}>
        <input
          autoComplete="new-password"
          id="newPassword"
          type="password"
          className={styles.form_input}
          required
          placeholder=" "
          value={form.newPassword}
          onChange={(event) =>
            setForm({ ...form, newPassword: event.target.value })
          }
        />
        <label htmlFor="newPassword" className={styles.form_label}>
          Новый пароль
        </label>
      </div>
      <div className={styles.input_container}>
        <input
          autoComplete="new-password"
          id="repeat"
          type="password"
          className={styles.form_input}
          required
          placeholder=" "
          value={form.repeat}
          onChange={(event) => setForm({ ...form, repeat: event.target.value })}
        />
        <label htmlFor="repeat" className={styles.form_label}>
          Повторите пароль
        </label>
      </div>
      <span className={styles.errorMessage}>
          {!validate.validate ? validate.errorMessage : ' '}
        </span>
      <button onClick={(e) => onSubmit(e)}>Сохранить</button>
    </div>
  );
};
