import { useState } from "react";
import { setNewPassword } from "../../store/Auth/Thunks/setNewPassword";
import styles from "./styles.module.css";
import { useDispatch } from "react-redux";

export const Profile = () => {
  const dispatch = useDispatch()
  const [validate, setValidate] = useState(true)

  const [form, setForm] = useState({
    password: '',
    newPassword: '',
    repeat: '',
  })

  const handleValidate = () => {
    const regularExpression = /^(?=.*[0-9])(?=.*[!@#$%^&*])[a-zA-Z0-9!@#$%^&*]{6,16}$/;
    if (form.newPassword !== form.repeat) {
      setValidate(false)
    }
    if (!form.newPassword || !form.password || !form.repeat) {
      setValidate(false)
    }

    if (form.password?.length < 8 || form.repeat?.length < 8) {
      setValidate(false)
    }

    if (!regularExpression.test(form.newPassword)) {
      setValidate(false)
    }

    return validate
  }

  const onSubmit = (e) => {
    e.preventDefault()
    if (handleValidate()) {
      dispatch(setNewPassword({pass: form.newPassword, id: localStorage.userId/1}))
    }
  } 

  return (
    <div className={styles.profile}>
      <h2 className={styles.title}>Сбросить пароль </h2>
      <div className={styles.input_container}>
          <input
            autoComplete="new-password"
            id="old"
            type="password"
            className={styles.form_input}
            required
            placeholder=" "
            value={form.password}
            onChange={(event) => setForm({...form, password: event.target.value})}
          />
          <label htmlFor="old" className={styles.form_label}>
            Старый пароль
          </label>
        </div>
        <div className={styles.input_container}>
          <input
            autoComplete="new-password"
            id="newPassword"
            type="password"
            className={styles.form_input}
            required
            placeholder=" "
            value={form.newPassword}
            onChange={(event) => setForm({...form, newPassword: event.target.value})}
          />
          <label htmlFor="newPassword" className={styles.form_label}>
            Новый пароль
          </label>
        </div>
        <div className={styles.input_container}>
          <input
            autoComplete="new-password"
            id="repeat"
            type="password"
            className={styles.form_input}
            required
            placeholder=" "
            value={form.repeat}
            onChange={(event) => setForm({...form, repeat: event.target.value})}
          />
          <label htmlFor="repeat" className={styles.form_label}>
            Повторите пароль
          </label>
        </div>
        <button onClick={(e) => onSubmit(e)} >Сохранить</button>
    </div>
  );
};
