import classNames from 'classnames';
import { useState } from 'react';
import styles from './styles.module.css';

export const EditPopup = ({ isPopupOpened, setIsPopupOpened }) => {
  const [email, setEmail] = useState('');
  const [name, setName] = useState('');
  const [phone, setPhone] = useState('');
  const [city, setCity] = useState('');
  const [password, setPassword] = useState('');
  const [role, setRole] = useState(roleVars.admin);

  const roleVars = {
    admin: 'admin',
    user: 'user',
    master: 'master',
  };
  return (
    <div
      className={classNames(styles.popup_wrapper, {
        [styles.popup_wrapper__opened]: true,
      })}
    >
      <div className={styles.popup}>
        <form className={styles.form}>
          <h2 className={styles.title}>Добавление пользователя</h2>
          <div className={styles.input_container}>
            <input
              autoComplete="new-password"
              id="name"
              type="text"
              className={styles.form_input}
              required
              placeholder=" "
              value={name}
              onChange={(event) => setName(event.target.value)}
            />
            <label htmlFor="name" className={styles.form_label}>
              Наименование
            </label>
          </div>
          <div className={styles.input_container}>
            <input
              autoComplete="new-password"
              id="email"
              type="text"
              className={styles.form_input}
              required
              placeholder=" "
              value={email}
              onChange={(event) => setEmail(event.target.value)}
            />
            <label htmlFor="email" className={styles.form_label}>
              Email
            </label>
          </div>
          <div className={styles.input_container}>
            <input
              autoComplete="new-password"
              id="phone"
              type="text"
              className={styles.form_input}
              required
              placeholder=" "
              value={phone}
              onChange={(event) => setPhone(event.target.value)}
            />
            <label htmlFor="phone" className={styles.form_label}>
              Телефон
            </label>
          </div>
          <div className={styles.input_container}>
            <input
              autoComplete="new-password"
              id="city"
              type="text"
              className={styles.form_input}
              required
              placeholder=" "
              value={city}
              onChange={(event) => setCity(event.target.value)}
            />
            <label htmlFor="city" className={styles.form_label}>
              Город
            </label>
          </div>
          <div className={styles.input_container}>
            <input
              autoComplete="new-password"
              id="password"
              type="password"
              className={styles.form_input}
              required
              placeholder=" "
              value={password}
              onChange={(event) => setPassword(event.target.value)}
            />
            <label htmlFor="password" className={styles.form_label}>
              Пароль
            </label>
          </div>
          <div className={styles.input_container}>
            <div className="radio">
              <label>
                <input type="radio" value={roleVars.user} onChange={setRole} checked={role===roleVars.user} />
                {roleVars.user}
              </label>
            </div>
            <div className="radio">
              <label>
                <input type="radio" value={roleVars.admin} onChange={setRole} checked={role===roleVars.admin}/>
                {roleVars.admin}
              </label>
            </div>
            <div className="radio">
              <label>
                <input type="radio" value={roleVars.master} onChange={setRole} checked={role===roleVars.master}/>
                {roleVars.master}
              </label>
            </div>
          </div>
        </form>
      </div>
    </div>
  );
};
