import classNames from "classnames";
import styles from "./styles.module.css";


export const InputRadioPeriod = ({label, value, setValue}) => {
    return (
        <button
        type="button"
        className={classNames(styles.perion_button, {
          [styles.period_button__active]: label.period === value,
        })}
        onClick={setValue}>
        {label.content}
      </button>
    )
}
