import { createSlice } from "@reduxjs/toolkit";

const initialState = {
  stantion: 1,
};

export const cartSlice = createSlice({
  name: "cart",
  initialState,
  reducers: {
    addOption: (state, action) => {
      const addedOption = action.payload;

      state[addedOption] = (state[addedOption] ? state[addedOption] : 0) + 1;

      return state;
    },
    removeOption: (state, action) => {
      const removedOption = action.payload;

      state[removedOption] =
        !state[removedOption] || state[removedOption] === 0
          ? 0
          : state[removedOption] - 1;

      return state;
    },
  },
});

export const cartSliceActions = cartSlice.actions;
