import styles from './styles.module.css'

export const Users = () => {
    const data = []

    for (let i=0; i< 100; i++) {
        data.push({title: 'UM System Group',
        email: 'example@mail.ru',
        number: '+77007007070',
        role: 'admin',
        franchise: 'Шымкент, Сарыагаш',
        balance: '250000'})
    }

    const headers = ['#'].concat(Object.keys(data[0]))
    
    return (
        data
        ? <div className={styles.table}>
            <div className={styles.table_header}>
                {headers.map((el, idx) => {
                    return (
                        <div key={idx} className={styles.table_title}>{el}</div> // ref to component with column idx for resize sync
                    )
                })}
            </div>
            <div className={styles.table_content}>
                {data.map((el, idx) => {
                    const cells = Object.values(el);

                    return (
                        <div className={styles.table_row}>
                            <div className={styles.table_cell}>{idx+1}</div>
                            {cells.map(cell => {
                                return <div className={styles.table_cell}>{cell}</div>
                            })}
                        </div>
                    )
                })}
            </div>
        </div>
        : <div>no data</div>
    )
}
